document.addEventListener('DOMContentLoaded', function() {
    var inputField = document.querySelector('[name="ExoNo"]');
    var maxLength = 13; // Adjust the maximum allowed length as needed

  

    inputField.addEventListener('blur', function(event) {
        var phoneNumber = event.target.value;

        if (phoneNumber.length < 6) {
            var validationMessage = document.createElement('span');
            validationMessage.className = 'wpcf7-not-valid-tip';
            validationMessage.setAttribute('aria-hidden', 'true');
            validationMessage.textContent = 'Please enter valid mobile no';

            // Insert the validation message below the input field
            event.target.parentNode.appendChild(validationMessage);
        }
    });

    inputField.addEventListener('focus', function(event) {
        // Clear and remove the validation message on focus
        var validationMessage = event.target.parentNode.querySelector('.wpcf7-not-valid-tip');
        if (validationMessage) {
            validationMessage.textContent = '';
            event.target.parentNode.removeChild(validationMessage);
        }
    });
   
});
</script>
