<style>
    body {margin:0; font-family:'Barlow', sans-serif;}
    p.pd-text label, p.pd-select label, p.pd-textarea label {display:none !important;}
    
    #pardot-form {margin:0; padding:0;}
    form.form {
        display:flex;
        flex-direction:column;
    }
    form.form p {margin:0 0 15px; padding:0;}
    form.form input[type="text"], form.form select {outline:none; border:none; padding:20px; color:#283742; background:#f5f7fd; font-family:'Roboto', sans-serif; font-weight:300; font-size:18px; width:100%; box-sizing:border-box; border-radius:10px;}
    form.form select {
        color:#224F5A;
    }
    form.form select.selected {
        color:#535353;
    }
    form.form select option {
        color:#535353;
    }

    form.form input::-webkit-input-placeholder { /* Chrome/Opera/Safari */
      color:#224F5A;
    }
    form.form input::-moz-placeholder { /* Firefox 19+ */
      color:#224F5A;
    }
    form.form input:-ms-input-placeholder { /* IE 10+ */
      color:#224F5A;
    }
    form.form input:-moz-placeholder { /* Firefox 18- */
      color:#224F5A;
    }
    
    form.form p.submit {margin:0 !important; order:2; text-align:center;}
    
    form.form p.submit input {margin:0; width:100%; padding:15px; box-sizing:border-box; outline:none; border:none; background:#43e39a; color:#224F5A; font-weight:bold; font-size:18px; cursor:pointer; -webkit-appearance: none; border-radius: 10px;transition:all 0.25s ease-in-out; font-family:'Outfit', sans-serif;}
    
    form.form p.submit input:hover, form.form p.submit input:active {
        background:#224F5A;
        color:#fff;
        transition:all 0.25s ease-in-out;
    }
    
    form.form p span.description {margin-top:10px; font-size:14px;}
    
